#include<iostream>
using namespace std;
class Teacher{ 
private:
    double salary;
public:
    //non parameterized constructor
        string name;
        string dept;
        string subject;
    //parameterized constructor
    Teacher(string n, string d, string s, double sal)
    {
        name = n;
        dept = d;
        subject = s;
        salary = sal;
    };
    void changedept(string newdept)
    {
        dept = newdept;}
    void getInfo()
        {  cout<<"name : "<<name<<endl;
         cout<<"dept : "<<dept<<endl;}

};
int main(){
    Teacher t1("shraddha", "CSE", "C++", 50000.0); //object creation
    t1.getInfo();
    return 0;	
}
    
//the above is the example of constructor overloading 